let isDarkMode = true; // Começa no modo escuro

const header = new FinisherHeader({
    "count": 100,
    "size": {
        "min": 2,
        "max": 8,
        "pulse": 0
    },
    "speed": {
        "x": {
            "min": 0,
            "max": 0.4
        },
        "y": {
            "min": 0,
            "max": 0.6
        }
    },
    "colors": {
        "background": "#000000", // Começa com fundo preto
        "particles": ["#fbfcca", "#d7f3fe", "#ffd0a7"]
    },
    "blending": "overlay",
    "opacity": {
        "center": 1,
        "edge": 0
    },
    "skew": 0, // Mantém reto
    "shapes": ["c"]
});

document.getElementById("toggleTheme").addEventListener("click", function () {
    isDarkMode = !isDarkMode; 


    header.init({
        "count": 100,
        "size": {
            "min": 2,
            "max": 8,
            "pulse": 0
        },
        "speed": {
            "x": {
                "min": 0,
                "max": 0.4
            },
            "y": {
                "min": 0,
                "max": 0.6
            }
        },
        "colors": {
            "background": isDarkMode ? "#000000" : "#FFFFFF", // Alterna fundo
            "particles": isDarkMode ? ["#fbfcca", "#d7f3fe", "#ffd0a7"] : ["#333333", "#555555", "#777777"] // Partículas para contraste
        },
        "blending": "overlay",
        "opacity": {
            "center": 1,
            "edge": 0
        },
        "skew": 0,
        "shapes": ["c"]
    });
});
